import { Component, OnInit } from '@angular/core';
import * as L from 'leaflet';
import { LocationService } from '../location.service';
import { HttpClient } from '@angular/common/http';
import { environment } from 'src/environments/environment';
@Component({
    selector: 'app-map',
    templateUrl: './map.component.html',
    styleUrls: ['./map.component.scss'],
})
export class MapComponent implements OnInit {
  map!: L.Map;
  note: string = '';
  selectedCoords: L.LatLng | null = null;
  private markersLayer: L.LayerGroup = L.layerGroup();

    constructor(private locationService: LocationService , private http: HttpClient) {}

    ngOnInit(): void {
      try {
        this.initMap();
        this.getLocations();  // Verileri almak için fonksiyonu çağırıyoruz
        this.loadMarkers();  // Sayfa yüklendiğinde marker'ları yükle
    } catch (error) {
        console.error('Harita yüklenemedi:', error);
    }
    }


    initMap(): void {
      this.map = L.map('map').setView([39.92077, 32.85411], 6);

      L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
        maxZoom: 19,
      }).addTo(this.map);
      this.markersLayer.addTo(this.map);  // Marker katmanını haritaya ekle
      // Click event to show popup
      this.map.on('click', (e: L.LeafletMouseEvent) => {
        const { lat, lng } = e.latlng;
        this.selectedCoords = e.latlng;
        const popupContent = `
          <div>
            <strong>Koordinatlar:</strong>
            <p>Lat: ${lat.toFixed(5)}, Lng: ${lng.toFixed(5)}</p>
            <textarea id="noteInput" placeholder="Not ekleyin..."></textarea><br>
            <button id="saveNote">Kaydet</button>
            <button id="deleteNote">Sil</button>
          </div>
        `;
        const popup = L.popup()
          .setLatLng(e.latlng)
          .setContent(popupContent)
          .openOn(this.map);

        // Attach event listeners to buttons inside popup
        setTimeout(() => {
          document.getElementById('saveNote')?.addEventListener('click', () => this.saveNote());
          document.getElementById('deleteNote')?.addEventListener('click', () => this.deleteNote());
        });
      });
    }

    saveNote(): void {
      const noteInput = (document.getElementById('noteInput') as HTMLTextAreaElement)?.value;
      if (this.selectedCoords && noteInput) {
        const data = {
          lat: this.selectedCoords.lat,
          lng: this.selectedCoords.lng,
          note: noteInput,
        };
        this.http.post('/api/locations', data).subscribe({
          next: () => alert('Not kaydedildi!'),
          error: (err) => console.error('Not kaydedilemedi:', err),
        });
      } else {
        alert('Lütfen bir not girin!');
      }
    }

    deleteNote(): void {
      if (this.selectedCoords) {
        this.http.delete(`/api/locations?lat=${this.selectedCoords.lat}&lng=${this.selectedCoords.lng}`).subscribe({
          next: () => alert('Not silindi!'),
          error: (err) => console.error('Not silinemedi:', err),
        });
      }
    }

     // Backend'den konumları al
  private getLocations(): void {
    this.http.get<any[]>('http://localhost:3000/locations').subscribe(locations => {
      locations.forEach(location => {
        this.addMarker(location);  // Her konumu haritada göster
      });
    });
  }

// Haritada bir marker ekle
private addMarker(location: any): void {
  // Latitude ve Longitude kontrolü
  if (location.latitude && location.longitude) {
    const marker = L.marker([location.latitude, location.longitude]).addTo(this.map);
    marker.bindPopup(`<b>Konum: </b>(${location.latitude}, ${location.longitude})<br><b>Not: </b>${location.note}`);
  } else {
    console.error('Geçersiz koordinatlar:', location);
  }
}
    testBackend(): void {
      this.http.get('http://localhost:3000/test-db', { responseType: 'text' })
          .subscribe(
              (response) => {
                  console.log('Backend bağlantısı başarılı:', response);
              },
              (error) => {
                  console.error('Backend bağlantısı hatası:', error);
              }
          );
  }

  loadMarkers(): void {
    // API'ye istek atarak veritabanındaki tüm marker'ları al
    this.http.get<any[]>('http://localhost:3000/locations').subscribe(locations => {
      this.markersLayer.clearLayers();  // Eski marker'ları temizle

      locations.forEach(location => {
        // Veritabanından gelen her konum için bir marker ekle
        const marker = L.marker([location.latitude, location.longitude])
          .bindPopup(`<b>Not:</b> ${location.note}`)
          .addTo(this.markersLayer);  // Katmana ekle
      });
    });
  }
}
