import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

export interface Marker {
  lat: number;
  lng: number;
  note: string;
}

@Injectable({
  providedIn: 'root',
})
export class MarkerService {
  private apiUrl = 'http://localhost:3000/locations'; // API URL'i

  constructor(private http: HttpClient) {}

  // Tüm işaretçileri al
  getMarkers(): Observable<Marker[]> {
    return this.http.get<Marker[]>(this.apiUrl);
  }

  // Yeni bir işaretçi ekle
  addMarker(marker: Marker): Observable<any> {
    return this.http.post(this.apiUrl, marker);
  }

  // Belirli bir işaretçiyi sil
  deleteMarker(lat: number, lng: number): Observable<any> {
    const url = `${this.apiUrl}?lat=${lat}&lng=${lng}`;
    return this.http.delete(url);
  }
}
