import { Component, OnInit } from '@angular/core';
import * as L from 'leaflet';
import { LocationService } from '../location.service';
import { HttpClient } from '@angular/common/http';
@Component({
    selector: 'app-map',
    templateUrl: './map.component.html',
    styleUrls: ['./map.component.scss'],
})
export class MapComponent implements OnInit {
  private map!: L.Map;
  private marker!: L.Marker;
  public note: string = '';
  public currentCoordinates: { lat: number; lng: number } | null = null;


    constructor(private locationService: LocationService , private http: HttpClient) {}

    ngOnInit(): void {
      try {
        this.initMap();
    } catch (error) {
        console.error('Harita yüklenemedi:', error);
    }
    }


    private initMap(): void {
      this.map = L.map('map').setView([39.92077, 32.85411], 6);

      L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
        maxZoom: 19,
      }).addTo(this.map);

      // Harita üzerinde tıklama olayını dinleme
      this.map.on('click', (e: L.LeafletMouseEvent) => {
        this.currentCoordinates = { lat: e.latlng.lat, lng: e.latlng.lng };

        // Marker oluşturma veya mevcut markeri güncelleme
        if (this.marker) {
          this.marker.setLatLng(e.latlng);
        } else {
          this.marker = L.marker(e.latlng).addTo(this.map);
        }

        // Popup oluştur ve göster
        this.marker.bindPopup(this.createPopupContent()).openPopup();
      });
    }

    // Popup içeriğini oluşturma
    private createPopupContent(): string {
      const lat = this.currentCoordinates?.lat.toFixed(5) ?? '';
      const lng = this.currentCoordinates?.lng.toFixed(5) ?? '';
      return `
        <div>
          <p><strong>Koordinatlar:</strong> ${lat}, ${lng}</p>
          <textarea id="noteInput" placeholder="Bir not ekleyin">${this.note}</textarea>
          <button onclick="window.saveNote()">Kaydet</button>
          <button onclick="window.deleteNote()">Sil</button>
        </div>
      `;
    }

    // Kaydetme fonksiyonunu globalde tanımla
    ngAfterViewInit(): void {
      (window as any).saveNote = () => this.saveNote();
      (window as any).deleteNote = () => this.deleteNote();
    }

    // Not kaydetme
    saveNote(): void {
      const input = document.getElementById('noteInput') as HTMLTextAreaElement;
      if (input) {
        this.note = input.value;
        alert('Not kaydedildi: ' + this.note);
      }
    }

    // Not silme
    deleteNote(): void {
      this.note = '';
      alert('Not silindi.');
      if (this.marker) {
        this.marker.bindPopup(this.createPopupContent()).openPopup();
      }
    }
    testBackend(): void {
      this.http.get('http://localhost:3000/test-db', { responseType: 'text' })
          .subscribe(
              (response) => {
                  console.log('Backend bağlantısı başarılı:', response);
              },
              (error) => {
                  console.error('Backend bağlantısı hatası:', error);
              }
          );
  }
}
